function getTenantId(req) {
  try {
    const h = (req && req.headers) ? req.headers : {};
    const q = (req && req.query) ? req.query : {};
    const b = (req && req.body) ? req.body : {};
    const tid =
      h["x-tenant-id"] ||
      q.tid || q.tenant ||
      b.tid || b.tenant ||
      "default";
    return String(tid || "default");
  } catch (_e) {
    return "default";
  }
}

