function sbDepositDue(job) {
  const { MonsterCore, CORE } = sbGetCore();
  const total = Number(job && job.total || 0);
  if (!MonsterCore || !CORE) return 0;
  try { return Number(MonsterCore.depositAmount(CORE, total) || 0); } catch { return 0; }
}

function sbCanSchedule(job) {
  const { MonsterCore, CORE } = sbGetCore();
  if (!MonsterCore || !CORE) return true; // if core not loaded, don't hard break your bot
  try { return Boolean(MonsterCore.canScheduleJob(CORE, job)); } catch { return true; }
}

function sbCanDoVariationWork(job) {
  const { MonsterCore, CORE } = sbGetCore();
  if (!MonsterCore || !CORE) return true;
  try { return Boolean(MonsterCore.canDoVariationWork(CORE, { approved: !!job.variation_approved })); } catch { return true; }
}
