// Optional deps (loaded safely if installed)
function safeRequire(mod) { try { return require(mod); } catch { return null; } }

// Fetch (Node 18+ has global fetch; fallback to node-fetch v2 if present)
let fetchFn = globalThis.fetch;
if (!fetchFn) {
  const nf = safeRequire("node-fetch");
  if (nf) fetchFn = nf;
}

const app = express();

