  PORT                 = 4000,
  NODE_ENV             = "production",
  ALLOW_ANY_ORIGIN     = "0",
  WEBSITE_ORIGIN,
  NGROK_ORIGIN,

  // Facebook
  VERIFY_TOKEN         = "landscaping123",
  FB_PAGE_ACCESS_TOKEN,

  // Admin header auth
  ADMIN_KEY,

  // CSRF
  CSRF_TOKEN           = "test-token",

