// ------------------- Helpers ------------------------------
const DATA_DIR    = path.join(process.cwd(), "data");
const TENANTS_DIR = path.join(DATA_DIR, "tenants");
const TMP_DIR     = path.join(TENANTS_DIR, "tmp");

[DATA_DIR, TENANTS_DIR, TMP_DIR].forEach(d => {
  if (!fs.existsSync(d)) fs.mkdirSync(d, { recursive: true });
});

function writeJSONAtomic(file, obj) {
  const tmp = file + ".tmp";
  fs.writeFileSync(tmp, JSON.stringify(obj, null, 2), "utf8");
  fs.renameSync(tmp, file);
}
function readJSON(file, fallback) {
  try {
    if (!fs.existsSync(file)) return fallback;
    const s = fs.readFileSync(file, "utf8");
    return s.trim() ? JSON.parse(s) : fallback;
  } catch { return fallback; }
}
function esc(s) {
  return String(s || "").replace(/[&<>\"']/g, c => ({
    "&": "&amp;", "<": "&lt;", ">": "&gt;", "\"": "&quot;", "'": "&#39;"
  }[c]));
}
function timingSafeEqualStr(a, b) {
  const A = Buffer.from(String(a));
  const B = Buffer.from(String(b));
  if (A.length !== B.length) return false;
  return crypto.timingSafeEqual(A, B);
}

// ------------------- Security / body parsers --------------
app.set("trust proxy", 1);
app.disable("x-powered-by");
app.use(helmet({
  crossOriginEmbedderPolicy: false,
  contentSecurityPolicy: NODE_ENV === "production" ? undefined : false
}));
app.use(express.json({ limit: "4mb" }));
app.use(express.urlencoded({ extended: true, limit: "4mb" }));
app.use(morgan("tiny"));
app.use(rateLimit({
  windowMs: 60_000,
  max: 120,
  standardHeaders: true,
  legacyHeaders: false
}));

// ------------------- CORS ---------------------------------
const allowlist = new Set([WEBSITE_ORIGIN, NGROK_ORIGIN].filter(Boolean));
app.use((req, res, next) => {
  const origin = req.headers.origin;
  const ok = origin && (allowlist.has(origin) || (ALLOW_ANY_ORIGIN === "1" && NODE_ENV !== "production"));
  if (ok) res.setHeader("Access-Control-Allow-Origin", origin);
  res.setHeader("Vary", "Origin");
  res.setHeader("Access-Control-Allow-Methods", "GET,POST,OPTIONS");
  res.setHeader("Access-Control-Allow-Headers", "Content-Type,Authorization,X-Admin-Key,X-CSRF-Token,X-Tenant-Id");
  if (req.method === "OPTIONS") return res.sendStatus(204);
  next();
});

