// ------------------- Monster namespace --------------------
const monster = {
  fs, path,
  dataDir: DATA_DIR,
  safeRequire,

  resolveTenantId(req) {
    const tid = (req.headers["x-tenant-id"] || req.query.tenantId || req.params?.tenantId || "default") + "";
    if (tid.includes("..") || tid.includes("/") || tid.includes("\\")) return "default";
    return tid.replace(/[^a-zA-Z0-9_\-]/g, "_");
  },

  ensureTenantDirs(tid) {
    const base = path.join(TENANTS_DIR, tid);
    const jobs = path.join(base, "jobs");
    const up   = path.join(base, "uploads");
    [base, jobs, up].forEach(d => { if (!fs.existsSync(d)) fs.mkdirSync(d, { recursive: true }); });
    return { base, jobs, up };
  },

  jobsFile(tid) { return path.join(this.ensureTenantDirs(tid).base, "jobs.json"); },
  readJobs(tid) { return readJSON(this.jobsFile(tid), []); },
  saveJobs(tid, list) { writeJSONAtomic(this.jobsFile(tid), list || []); },

  findJob(tid, id) {
    return (this.readJobs(tid) || []).find(j => String(j.id) === String(id)) || null;
  },
  upsertJob(tid, job) {
    const list = this.readJobs(tid);
    // strip prototype pollution keys
    ["__proto__", "constructor", "prototype"].forEach(k => { if (Object.hasOwn(job, k)) delete job[k]; });
    const i = list.findIndex(x => String(x.id) === String(job.id));
    if (i >= 0) list[i] = job; else list.push(job);
    this.saveJobs(tid, list);
    return job;
  },

  addRoute(method, p, handler) {
    const layer = app._router?.stack || [];
    const exists = layer.some(s => s.route && s.route.path === p && s.route.methods?.[method]);
    if (!exists) app[method](p, handler);
  },

  requireAdmin(req, res, next) {
    const key = ADMIN_KEY || "";
    if (!key) return res.status(503).json({ ok: false, error: "ADMIN_KEY not configured" });
    const hdr = (req.headers["x-admin-key"] || "").trim();
    if (!timingSafeEqualStr(hdr, key)) return res.status(401).json({ ok: false, error: "unauthorized" });
    next();
  }
};

// ------------------- CSRF (state-changing) ----------------
function requireCSRF(req, res, next) {
  const token = (req.headers["x-csrf-token"] || "").trim();
  if (!token || token !== CSRF_TOKEN) return res.status(403).json({ ok: false, error: "CSRF token missing or invalid" });
  next();
}

