// Safe date helpers
function sbParseIso(s) {
  try { return s ? new Date(String(s)) : null; } catch { return null; }
}
function sbDaysSince(iso) {
  const d = sbParseIso(iso);
  if (!d || isNaN(d.getTime())) return null;
  const ms = Date.now() - d.getTime();
  return Math.floor(ms / (24 * 60 * 60 * 1000));
}
function sbEnsureArr(x) { return Array.isArray(x) ? x : []; }

// FB sender
function sbSendFbText(senderId, messageText) {
  try {
    const token = process.env.FB_PAGE_ACCESS_TOKEN || "";
    const url = "https://graph.facebook.com/v19.0/me/messages?access_token=" + encodeURIComponent(token);
    const payload = { recipient: { id: String(senderId) }, message: { text: String(messageText || "") } };

    return fetch(url, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(payload)
    })
    .then(async (r) => {
      const t = await r.text().catch(() => "");
      if (!r.ok) throw new Error("fb_send_failed status=" + r.status + " body=" + t);
      return { ok: true, status: r.status, body: t };
    });
  } catch (e) {
    return Promise.reject(e);
  }
}

// Build reminder plan from MonsterCore if available, else fallback
function sbReminderPlan() {
  const MonsterCore = global.MonsterCore;
  const CORE = global.MONSTER_CORE;

  if (MonsterCore && CORE && CORE.payment_chasing && CORE.payment_chasing.schedule) {
    return CORE.payment_chasing.schedule.map(x => ({ day: Number(x.day), type: String(x.type) }));
  }
  return [
    { day: 1, type: "friendly" },
    { day: 7, type: "firm" },
    { day: 14, type: "final_notice" },
    { day: 21, type: "escalation_warning" }
  ];
}

// Message templates (edit later)
function sbReminderText(job, item) {
  const id = job && job.id ? String(job.id) : "";
  const inv = job && job.invoice_number ? String(job.invoice_number) : "";
  const total = job && job.total ? Number(job.total) : 0;

  if (item.type === "friendly") {
    return "Hi, just a friendly reminder your invoice" + (inv ? " " + inv : "") + " for job " + id + " is still outstanding. Total $" + total + ". Reply if you need anything.";
  }
  if (item.type === "firm") {
    return "Hi, following up again: invoice" + (inv ? " " + inv : "") + " for job " + id + " is now overdue. Total $" + total + ". Please arrange payment today or reply to discuss.";
  }
  if (item.type === "final_notice") {
    return "Final notice: invoice" + (inv ? " " + inv : "") + " for job " + id + " remains unpaid. Total $" + total + ". Please pay immediately or contact us today.";
  }
  if (item.type === "escalation_warning") {
    return "Invoice" + (inv ? " " + inv : "") + " for job " + id + " is severely overdue. Total $" + total + ". If not resolved we may escalate recovery and pause further work.";
  }
  return "Reminder: invoice for job " + id + " is outstanding. Total $" + total + ".";
}

// Compute due reminders for a job today
function sbDueRemindersForJob(job) {
  if (!job) return [];
  if (!job.invoice_sent_at) return [];
  if (job.invoice_paid_at) return [];

  const hasFb = !!(job.fb_sender_id || (job.contact && job.contact.facebook));

  // Force mode (testing): treat as due immediately if invoice sent + not paid + has FB id
  if (global.SB_FORCE_REMINDERS) {
    if (hasFb) {
      return [{ key: "force-0", day: 0, type: "force" }];
    }
    return [];
  }

  const days = sbDaysSince(job.invoice_sent_at);
  if (days === null) return [];

  const plan = sbReminderPlan();
  const sent = sbEnsureArr(job.reminders_sent);

  const due = [];
  for (const item of plan) {
    if (days >= Number(item.day)) {
      const key = String(item.type) + ":" + String(item.day);
      if (!sent.includes(key)) {
        due.push({ key, day: Number(item.day), type: String(item.type) });
      }
    }
  }
  return due;
}
