(function(){
  const multer = monster.safeRequire("multer");
  const upload = multer ? multer({
    dest: TMP_DIR,
    limits: { fileSize: 5 * 1024 * 1024 }, // 5 MB
    fileFilter: (_req, file, cb) => {
      if (/\.(php|html|exe|sh|js|bat|cmd)$/i.test(file.originalname)) return cb(new Error("Prohibited file type"), false);
      cb(null, true);
    }
  }) : null;

  function handler(req, res){
    try {
      let imgs = [];
      if (req.files?.length) imgs = req.files.map(f => f.path);
      else if (Array.isArray(req.body?.imageUrls)) imgs = req.body.imageUrls;

      const tags = [];
      imgs.forEach(s => {
        const t = String(s).toLowerCase();
        if (t.includes("tree")) tags.push("tree");
        if (t.includes("drive")) tags.push("driveway");
        if (t.includes("rock")) tags.push("excavation");
      });

      const base = 1200 + imgs.length * 50;
      const total = base;
      res.json({ ok:true, images: imgs, tags, estimate: { total, base } });
    } catch (e) {
      console.error("[photo-quote] error", e);
      res.status(500).json({ ok:false, error:String(e) });
    }
  }

  if (upload) app.post("/photo-quote", upload.array("photos", 6), handler);
  else app.post("/photo-quote", handler);
})();

