  function nextBusinessDay(d){
    const dt = new Date(d.getTime());
    dt.setDate(dt.getDate() + 1);
    if (dt.getDay() === 0) dt.setDate(dt.getDate() + 1);
    if (dt.getDay() === 6) dt.setDate(dt.getDate() + 2);
    return dt;
  }

  monster.findNextSlot = function(tid){
    const jobs = monster.readJobs(tid);
    const byDay = {};
    for (const j of jobs) {
      const day = (j.scheduled_for || "").slice(0,10);
      if (day) byDay[day] = (byDay[day] || 0) + 1;
    }
    let check = new Date();
    for (let k=0;k<60;k++){
      const key = check.toISOString().slice(0,10);
      if (check.getDay()>0 && check.getDay()<6){
        const used = byDay[key] || 0;
        if (used < 2) return { date:key, startHour: (used===0?HOURS.start:(HOURS.start+HOURS.slot_hours)) };
      }
      check = nextBusinessDay(check);
    }
    return null;
  };

  monster.generateICS = function(job){
    const id = String(job.id);
    let dt  = (job.scheduled_for || "").replace(/[-:]/g,"").replace(/\.\d+Z?$/,"");
    if (!dt) dt = new Date().toISOString().replace(/[-:]/g,"").replace(/\.\d+Z?$/,"");
    const lines = [
      "BEGIN:VCALENDAR","VERSION:2.0","PRODID:-//Monster//EN","BEGIN:VEVENT",
      "UID:"+id+"@monster","DTSTAMP:"+dt,"DTSTART:"+dt,"DTEND:"+dt,
      "SUMMARY:Job "+id+" ("+(job.customer_name||"")+")",
      "LOCATION:"+(job.address||""),
      "DESCRIPTION:Auto-scheduled by Monster","END:VEVENT","END:VCALENDAR"
    ];
    return lines.join("\r\n");
  };

