// Admin: send due reminders (Facebook only) and log keys to prevent duplicates
monster.addRoute("post", "/admin/reminders/send", (req, res) => {
  try {
    const tid = getTenantId(req);
    const jobs = monster.readJobs(tid) || [];
    const sendPromises = [];
    const sentLog = [];

    for (const j of jobs) {
      const due = sbDueRemindersForJob(j);
      if (!due.length) continue;

      const senderId = j.fb_sender_id;
      if (!senderId) {
        sentLog.push({ jobId: j.id, skipped: true, reason: "missing_fb_sender_id", due });
        continue;
      }

      j.reminders_sent = Array.isArray(j.reminders_sent) ? j.reminders_sent : [];

      for (const item of due) {
        const msg = sbReminderText(j, item);
        const p = sbSendFbText(senderId, msg)
          .then(() => {
            j.reminders_sent.push(item.key);
            sentLog.push({ jobId: j.id, sent: true, key: item.key, type: item.type, day: item.day });
          })
          .catch((e) => {
            sentLog.push({ jobId: j.id, sent: false, key: item.key, error: String(e) });
          });

        sendPromises.push(p);
      }

      monster.upsertJob(tid, j);
    }

    Promise.allSettled(sendPromises).then(() => {
      res.json({ ok:true, attempted: sendPromises.length, log: sentLog });
    });

  } catch (e) {
    res.status(500).json({ ok:false, error:String(e) });
  }
});
